import React, { Component } from "react";
import Popup from "reactjs-popup";


import "../../modal.css";


class ShiftPattern extends Component {

	constructor(props) {
		super(props);
		this.handleShiftTypeChange = this.handleShiftTypeChange.bind(this);
		this.handleContinue = this.handleContinue.bind(this);
	}

	state = {
		shiftTimeOfDay: "Day",
		shiftStartTime: "08:00",
		shiftEndTime: "20:00"
	};
	

	render() {
		return (
			
			<div class="container">

				<Popup 
					trigger={<button className="button"> Open Modal </button>}
					modal
					closeOnDocumentClick
				>

				<div class="text-center header">
					<h3>Progress</h3>
					<progress value="0" max="100"></progress>
				</div>

				<div>
					<br></br>
					<h5>Please fill in all fields.</h5>
					<br></br>
					<br></br>
				</div>

				<div class="wrapper">
					<div class="inline">
						<h6>Day or Night shift:</h6>
					</div>
					<div class="inline">
						<select selected={this.state.shiftTimeOfDay} onChange={this.handleShiftTypeChange} name="Shift type">
							<option value="Day">Day</option>
							<option value="Night">Night</option>
						</select>
					</div>
				</div>


				<div class="wrapper">
					<div class="inline">
						<h6>Shift start time:</h6>
					</div>
					<div class="inline">
							<input step="3600" type="time"/>
					</div>
				</div>

				<div class="wrapper">
					<div class="inline">
						<h6>Shift end time:</h6>
					</div>
					<div class="inline">
							<input step="3600" type="time"/>
					</div>
				</div>


				<div class="wrapper text-center">
					<div class="inline">
						<button disabled>Previous</button>
					</div>
					<div class="inline">
							<button onClick={() => this.handleContinue()}>Continue</button>
					</div>
				</div>				
			
				</Popup>
				



    	</div>	

		);
	}

	handleShiftTypeChange(uiObj) {
		//Day/Night from UI element
		const value = uiObj.target.value;

		//Set user input to state
		this.setState({shiftTimeOfDay: value});	

		//Update other states based on input
		if (value === "Day") {
			this.setState({shiftStartTime: "08:00"});
		} else {
			this.setState({shiftEndTime: "20:00"});
		}
	}

	handleContinue() {
		console.log("Button clicked");

		const shiftStartTime = this.state.shiftStartTime;
		const shiftEndTime = this.state.shiftEndTime;

		//Validation
		if (this.state.shiftTimeOfDay === "Day") {
			if (shiftStartTime > shiftEndTime) {
				console.log("error");
			}
		} else {
			if (shiftStartTime < shiftEndTime) {
				console.log("error");
			}

		}
	}



}



export default ShiftPattern;
