	state = {
		uuid: '',
		shiftTimeOfDay: '',
		shiftStartTime: '',
		shiftEndTime: '',
		isReadyToClose: false
	};


	
	render() {
		return (
			
			<div className="container">
				<h4>Test {this.state.uuid}</h4>

				
				
